class StartConsole():        statusCode = 0                def __init__(self):                self.onCreate(self)                self.notCancel(self)        @staticmethod        def onCreate(self):              None        @staticmethod        def notCancel(self):               print("status code " + str(self.statusCode))               print("\nНажмите 'Enter' для выхода")               input()               class UpdateConsole():        def __init__(self):                self.onCreate(self)                self.update(self)        @staticmethod        def onCreate(self):              None        @staticmethod        def update(self):                while 1:                        self.onUpdate(self)        @staticmethod        def onUpdate(self):                None