from ctypes import windll, create_string_bufferclass OutputConsole:        sizeX, sizeY = 80, 25        statusCode = 0        status = "ok"        def __init__(self):                h = windll.kernel32.GetStdHandle(-12)                csbi = create_string_buffer(22)                res = windll.kernel32.GetConsoleScreenBufferInfo(h, csbi)                if res:                    import struct                    (bufx, bufy, curx, cury, wattr,                     left, top, right, bottom, maxx, maxy) = struct.unpack("hhhhHhhhhhh", csbi.raw)                    self.sizeX = right - left + 1                    self.sizeY = bottom - top + 1                else:                        self.statusCode = 1                        self.status = "не получилось узнать размер консоли"        def printLineX(self):                i = 0                while i < self.sizeX:                        print("-", end = "")                        i += 1                if(self.statusCode == 1):                        print()        def printLineY(self):                i = 0                while i < self.sizeY:                        print()                        i += 1        def printLineWithSymbolX(self, symbol):                i = 0                while i < self.sizeX:                        print(str(symbol), end = "")                        i += 1